mapper.insert:
insert into qna (
	user_no, qtype, title, content, regdate
	) values (
	#{user_no}, #{qtype}, #{title}, #{content}, now()
)

	select last_insert_id()

insert into qna (
	user_no, manager_no, title, content, replydate, gno, ono, depth
) values (
	#{user_no}, #{manager_no}, #{title}, #{content}, now(), #{gno}, #{ono}, #{depth}
)

mapper.update:
update qna set
	title = #{title}, content = #{content}
where no = #{no}
