mapper.insert:
INSERT INTO user (
	name, birth, nik_name, selfi, email, tel, pwd, addr1, addr2, height, weight, activity, intro
) VALUES (
	#{name}, #{birth}, #{nik_name}, #{selfi}, #{email}, #{tel}, #{pwd}, #{addr1}, #{addr2}, #{height}, #{weight}, #{activity}, #{intro}
)

	SELECT LAST_INSERT_ID()

insert into prefer (user_no, prefer_food) 
	select no, prefer from user
where no = 1 and prefer = "국";
